	public Boolean negotiatePossible(WorldState w) {
		if (visited || w.loopComplete) {
			return false;
		}
		if (w.day > 1 && w.day < 50 - w.eventOffset*3 && cVirg == false && aVirg == false && vVirg && modest && debased == false && timesSlaughtered() == 0 && timesStripped() == 0) {
			for (int i = 0; i < 3; i++) {
				if (w.getCast()[i] == null) {
					return false;
				} else if (w.getCast()[i].ANGST < Project.billion) {
					return false;
				}
			}
			return true;
		}
		return false;
	}
	
