	public Boolean totalNecessary(long[] input) {
		int count = 0;
		for (int i = 0; i < input.length; i++) {
			if (input[i] != 1) {
				count++;
			}
		}
		if (count > 1) {
			return true;
		} else {
			return false;
		}
	}
	
	public int lowestTraumaLevel() {
		int value = getFEARLevel() + FANTlevels[0];
		if (getDISGLevel() + FANTlevels[1] < value) {
			value = getDISGLevel() + FANTlevels[1];
		}
		if (getPAINLevel() + FANTlevels[2] < value) {
			value = getPAINLevel() + FANTlevels[2];
		}
		if (getSHAMLevel() + FANTlevels[3] < value) {
			value = getSHAMLevel() + FANTlevels[3];
		}
		return value;
	}
	
	public int highestCircumstanceLevel() {
		int value = getHATELevel();
		if (getPLEALevel() > value) {
			value = getPLEALevel();
		}
		if (getINJULevel() > value) {
			value = getINJULevel();
		}
		if (getEXPOLevel() > value) {
			value = getEXPOLevel();
		}
		return value;
	}
	
