	public String fixedFormat(long amount) {
		if (amount < 0) {
			amount = 0 - amount;
		}
		int power = 3;
		while (amount >= 10000) {
			amount = amount/10;
			power++;
		}
		if (power == 3) {
			if (amount < 10) {
				power = 0;
			} else if (amount < 100) {
				power = 1;
			} else if (amount < 1000) {
				power = 2;
			}
		}
		String format = "";
		if (power < 4 || (power - 2) % 3 == 0) {
			format = format + " ";
		}
		if (power > 2) {
			format = format + amount/1000;
		} else {
			format = format + " ";
		}
		if (power % 3 == 0 && power > 3) {
			format = format + ".";
		}
		if (power > 1) {
			format = format + (amount%1000)/100;
		} else {
			format = format + " ";
		}
		if ((power - 1) % 3 == 0 && power > 3) {
			format = format + ".";
		}
		if (power > 0) {
			format = format + (amount%100)/10;
		} else {
			format = format + " ";
		}
		if (power < 4) {
			format = format + amount%10;
		}
		if (power > 17) {
			format = format + "E";
		} else if (power > 14) {
			format = format + "P";
		} else if (power > 11) {
			format = format + "T";
		} else if (power > 8) {
			format = format + "G";
		} else if (power > 5) {
			format = format + "M";
		} else if (power > 3) {
			format = format + "k";
		}
		/*if ((power - 2) % 3 == 0 && power != 2) {
			format = format + " ";
		}*/
		return format;
	}
	
