    public int getArrivalTime(Chosen target, Chosen other)
    {
        int time = 600 - target.dignity * 2;
        time = (time * (250 - other.confidence)) / 200;
        int analysis = getRelationship(target.number, other.number);
        if(analysis == 1)
            time = (time * 9) / 10;
        else
        if(analysis == 2)
            time = (time * 6) / 7;
        else
        if(analysis == 4)
            time = (time * 4) / 5;
        else
        if(analysis == -2)
            time = (time * 7) / 6;
        else
        if(analysis == -4)
            time = (time * 5) / 4;
        return time;
    }

