    public Boolean legalSpread(int statSeed[], Boolean customized)
    {
        Boolean goodStats = true;
        for(int i = 0; i < 4; i++)
        {
            int first = statSeed[i] / 33 + (statSeed[i] % 33 != 0 ? 1 : 0);
            if(first < 1)
                first = 1;
            else
            if(first > 3)
                first = 3;
            int second = statSeed[i + 4] / 33 + (statSeed[i + 4] % 33 != 0 ? 1 : 0);
            if(second < 1)
                second = 1;
            else
            if(second > 3)
                second = 3;
            int third = statSeed[i + 8] / 33 + (statSeed[i + 8] % 33 != 0 ? 1 : 0);
            if(third < 1)
                third = 1;
            else
            if(third > 3)
                third = 3;
            if(first == second || first == third || second == third)
                goodStats = false;
        }

        for(int i = 0; i < 4 && !customized; i++)
        {
            int first = statSeed[i] / 25 + (statSeed[i] % 25 != 0 ? 1 : 0);
            if(first < 1)
                first = 1;
            int second = statSeed[i + 4] / 25 + (statSeed[i + 4] % 25 != 0 ? 1 : 0);
            if(second < 1)
                second = 1;
            int third = statSeed[i + 8] / 25 + (statSeed[i + 8] % 25 != 0 ? 1 : 0);
            if(third < 1)
                third = 1;
            if(first == second || first == third || second == third)
                goodStats = false;
        }

        Boolean extremeLow = false;
        Boolean extremeHigh = false;
        Boolean midValue = false;
        for(int i = 0; i < 4; i++)
            if(statSeed[i] > 66)
                extremeHigh = true;
            else
            if(statSeed[i] < 34)
                extremeLow = true;
            else
                midValue = true;

        if(!extremeLow || !extremeHigh || !midValue)
            goodStats = false;
        extremeLow = false;
        extremeHigh = false;
        midValue = false;
        for(int i = 4; i < 8; i++)
            if(statSeed[i] > 66)
                extremeHigh = true;
            else
            if(statSeed[i] < 34)
                extremeLow = true;
            else
                midValue = true;

        if(!extremeLow || !extremeHigh || !midValue)
            goodStats = false;
        extremeLow = false;
        extremeHigh = false;
        midValue = false;
        for(int i = 8; i < 12; i++)
            if(statSeed[i] > 66)
                extremeHigh = true;
            else
            if(statSeed[i] < 34)
                extremeLow = true;
            else
                midValue = true;

        if(!extremeLow || !extremeHigh || !midValue)
            goodStats = false;
        return goodStats;
    }

